// (a + bi)^2 = a^2 - b^2 + 2abi
vec2 squareComplexNumber(vec2 complexNumber) {
    float realComponentOfProduct = (complexNumber.x * complexNumber.x) - (complexNumber.y * complexNumber.y);
    float imaginaryComponentOfProduct = 2.0 * complexNumber.x * complexNumber.y;
    return vec2(realComponentOfProduct, imaginaryComponentOfProduct);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 rainbow[7];
    rainbow[0] = vec4(1.0, 0.0, 0.0, 1.0); // Red
    rainbow[1] = vec4(1.0, 0.5, 0.0, 1.0); // Orange
    rainbow[2] = vec4(1.0, 1.0, 0.0, 1.0); // Yellow
    rainbow[3] = vec4(0.0, 1.0, 0.0, 1.0); // Green
    rainbow[4] = vec4(0.0, 0.0, 1.0, 1.0); // Blue
    rainbow[5] = vec4(0.294117647, 0.0, 0.509803922, 1.0); // Indigo (according to Wikipedia)
    rainbow[6] = vec4(0.5, 0.0, 1.0, 1.0); // Violet (according to Wikipedia)

	float zoom = 2.0;
    vec2 c = vec2(sin(iTime), cos(iTime));
    //vec2 c = vec2(-1.0, 0.0);

    vec2 z = fragCoord.xy / iResolution.xy;
    z = z * vec2(zoom * 2.0);
    z = z - vec2(zoom);

    fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    for (int i = 0; i < 14; i++) {
        z = squareComplexNumber(z) + c;

    	if (abs(z.x) < 2.0 && abs(z.y) < 2.0) {
            fragColor = rainbow[i / 2];
    	}
    }
}
