void mainImage( out vec4 O, in vec2 p )
{
    p = (p - .5 * iResolution.xy) / iResolution.x;
    float l = length(p), 
          k = floor(mod(3. * (atan(-p.x,p.y) + acos(min(.1 / l,1.)))/(6.28)-1.,3.));
	O = step(l,.2) * mix(
		vec4(k,2. - k,0,1),
		1. - step(l,.08) * vec4(.7,.5,0,0),
		step(l,.1)
	); 
}
