const float ITER = 100.0;
float INFI = 26.0;

const float FRminX = -0.8;
const float FRminY = -0.6;
const float FRmaxX = 0.8;
const float FRmaxY = 0.6;

const float FRangeX = FRmaxX - FRminX;
const float FRangeY = FRmaxY - FRminY;

const float C = 0.279;
const float dC = 0.005;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float newC = C + iTime * dC;
    float N=0.0;
    vec2 P = vec2(FRminX+FRangeX*(fragCoord.x/iResolution.x),
                  FRminY+FRangeY*(fragCoord.y/iResolution.y));

    for(float n=0.0; n<ITER; ++n){
        float aa = P.x * P.x;
        float bb = P.y * P.y;
        P.x = aa - bb + newC * 0.85;
        P.y = 2.0 * P.x * P.y * 0.85;
        if (aa + bb > INFI) {
            N = n;
            break;
        }
    }

    float g = N / INFI / 10.0;
    fragColor = vec4(g*3.0, g*5.0, g*7.0, 1.0);
}
