void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;

    fragColor = vec4 ( vec3(0.0), 1.0);    

    //glow test

    fragColor = vec4(0.0,0.0,(1.0-abs(uv.y))*sin(iTime),1.0);

    float amp = 0.05*(clamp(sin(iTime),0.0,1.0));

    //sine wave
    if (uv.y > sin(uv.x*40.0+iTime*10.0)*amp 
        && 
        uv.y < 0.01+(sin(uv.x*40.0+iTime*10.0)*amp)) 
        fragColor = vec4(0.7,0.7,1.0,1.0);

	vec4 black =  vec4 (0.0,0.0,0.0,1.0);
    vec4 green =  vec4 (0.6,0.8,0.1,1.0);
    vec4 orange =  vec4 (0.8,0.4,0.1,1.0);
    vec4 blue = vec4(0.216, 0.471, 0.698,1.0);

    //Green Circle    
    vec2 center1 = vec2 ((0.1*cos(float(iFrame)/50.0)) ,(0.1*sin(float(iFrame)/50.0)));

    vec2 d = uv - center1;

    float rad = 0.8;

    if (length(d) < rad)
    {
        vec2 d2 = d + vec2(0.2,-0.3);
        green = vec4(0.6,0.8,0.1,1.0)-0.9*vec4(vec3(sin(length(dot(d2,d)))),1.0);
        fragColor = green;
    }

    //Orange Circle           
    vec2 center2 = vec2((0.1*sin(float(iFrame)/60.0)) ,(0.1*cos(float(iFrame)/50.0)));

    d = uv - center2;

    if (length(d) < 0.3)
    {
        vec2 d2 = d + vec2(0.05,-0.1);
        orange = vec4(0.8,0.4,0.1,1.0) - vec4(vec3(sin(length(d2))),1.0);
        fragColor = orange;
    }

    //Blue Circle
    vec2 center3 = vec2 (0.8*sin(float(iFrame)/20.0)*2.0 ,cos(float(iFrame)/100.0)*0.8 + 0.2*(sin(float(iFrame)/8.0)));

    d = uv - center3;

    if (length(d) < 0.6) 
    {
        vec2 d2 = d + vec2(0.05,-0.2);
        blue = vec4(0.216, 0.471, 0.698,1.0) - 0.7*vec4(vec3(sin(length(d2))),1.0);
        fragColor = blue;
    }
}
