#define PI 3.14159
#define FLAME_COLOR vec3(1.0, 0.4, 0.2);

vec3 pal(float t, vec3 a, vec3 b, vec3 c, vec3 d){
	return a + b * cos(2.0*PI*(c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor += vec4(texture(iChannel0, uv).rgba);

    vec3 gradient = pal(
        (1.0-fragColor.r)/2.0,
        vec3(0.5),
        vec3(0.5),
        vec3(1.0),
        vec3(0.0, 0.1, 0.2)
    );

    fragColor.rgb *= gradient;

    if(fragColor.b > fragColor.r*2.0 && fragColor.b > 0.5){
    	fragColor.rgb = vec3(1.0);
    }

    fragColor.rgb = clamp(fragColor.rgb,vec3(0.0), vec3(1.0));
}

//based on https://www.shadertoy.com/view/MtySzd

#define FONT_SIZE 3.0;
#define FONT_SPACE 0.6;

vec2 tp = vec2(0.0);

#define C(cp) c+= getChar(cp, tp); tp.x -= FONT_SPACE;

#define _H C(184);
#define _Y C(169);
#define _P C(160);
#define _O C(191);
#define _T C(164);
#define _E C(181);

vec4 getChar(int ch, vec2 nm){ //row column, normalized range
    if (nm.x<0.|| nm.x>1. || nm.y<0.|| nm.y>1.) return vec4(0.0);
    vec2 rc = vec2(mod(float(ch), 16.0), floor(float(ch) / 16.0));
    vec2 dv = (nm+rc)/16.;
    return texture(iChannel0,dv).rrrr;
}

vec4 text(vec2 pos){
	vec4 c= vec4(0.0);
    tp += pos;
    _H _Y _P _O _T _H _E _T _E
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    tp = fragCoord.xy;
    tp /= iResolution.y;
    tp *= FONT_SIZE;

    tp += vec2(0.23,-1.0);

    //trail effect
    vec2 dv = uv;
    float wobble = 2.0 * (texture(iChannel2, uv+vec2(0,iTime/10.0)).r-0.5);
    dv.y -= 1.0 / iResolution.y;
    dv.x += (wobble)/iResolution.x;
    fragColor = texture(iChannel1, dv);
    fragColor.rgb -= 0.01 * wobble + 0.01;
    fragColor.rgb = clamp(fragColor.rgb, vec3(0.0), vec3(1.0));

    fragColor += text(vec2(0.0, 1.0));    
}
