void mainImage( out vec4 FO, in vec2 FI ) {
	FO = texture(iChannel0, FI / iResolution.xy);
}

const int MaxSteps	= 128;	// max number of ray steps (affects draw dist & fog)
const float Step	= 0.2;	// amount to advance ray (affects draw dist & fog)
const float Bailout	= 0.1;	// transparency bailout: higher==faster but less precise
const float CMix	= 0.63;	// amount to mix color from previous frames (0.0-1.0)(higher==nicer but w/ more motion blur)
const float Speed	= 1.5;	// camera z-speed
const float Fuzzy	= 0.5;	// cloud fuzziness (0.0-0.5)

#define iGT iTime

// hash without sine
// https://www.shadertoy.com/view/4djSRW
#define MOD3 vec3(443.8975, 397.2973, 491.1871) // uv range
float hash13(vec3 p3) {
	p3  = fract(p3 * MOD3);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}
vec2 hash21(float p) {
	vec3 p3 = fract(vec3(p) * MOD3);
	p3 += dot(p3, p3.yzx + 19.19);
	return fract(vec2((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y));
}

// spiral cloud tunnel
float map(in vec3 p) {
    p *= .1;
    p.z *= 16.;
    float f =
        mix(
            texture(iChannel0, p.xy-hash21(floor(p.z))).r,
            texture(iChannel0, p.xy-hash21(floor(p.z)+1.)).r,
            smoothstep(0., 1., fract(p.z))
		);
    p.xy += .12 * vec2(sin(.2*p.z), cos(.2*p.z));
    f += 4. * length(p.xy) - 1.;
    return clamp(f, 0., 1.);
}

void mainImage(out vec4 FO, in vec2 FI) {
	vec2 res = iResolution.xy;
   	vec2 uv = FI / res, p = (FI-res/2.) / res.y;

    vec3 ray=vec3(0.), rTmp, rDir = normalize(vec3(p, 1.)), col = vec3(0.);
	float tr=1., v;

    // mousing around
    if(iMouse.z>0.)
    	ray.xy -= 5. * (iMouse.xy/res-.5);

    // z-movement
    ray.z += Speed * iGT;

    // advance ray & accumate color
    for(int i=0; i<MaxSteps; i++) {

        // bail out if sample contribution is low (based on transparency)
        if(tr<Bailout)
            break;

        // temporary ray position (jittered along rayDir)
        rTmp = ray + 2. * Step * rDir * (.5-hash13(ray+mod(.1*iGT, 100.)));

        // volume sample
        v = smoothstep(.5-Fuzzy, .5+Fuzzy, map(rTmp));
        //v = map(rayTmp);

        // add a shaded, colored sample (proportionate to transparency)
        col += 1.2 * tr * v * vec3(1., .7, .6) * (1.-1.2*map(rTmp*vec3(.85, .85, 1.)-vec3(0., .0, -.3)));

        // add fog (proportionate to transparency & MaxSteps)
        col += 1.4 * tr * vec3(.7, .8, 1.) / float(MaxSteps);

        // adjust bailout/mixing variable
        tr -= v;

        // advance ray position
        ray += Step * rDir;
    }

    // mix this frame's color with last frame's color
    col = mix(col, texture(iChannel1, uv).rgb, CMix);

	FO = vec4(col, 1.);
}
