vec2 bez(mat3 bez1,mat3 bez2,float p)
{
    float mp=1.-p,z;
    vec3 p11,p12,p1;
    vec3 p21,p22,p2;

    p11=bez1[0].xyz*p+bez1[1].xyz*mp;
    p12=bez1[1].xyz*p+bez1[2].xyz*mp;
    p1=p11*p+p12*mp;

    return vec2(p1.xy);
}

void mainImage( out vec4 C, in vec2 uv )
{
    vec3 v0=vec3(0);
    mat3 b1=mat3(.2,.3,0,.1+cos(iDate.w)/2.,0,0,0,.1+sin(iDate.w)/4.,0);
    mat3 b2=b1+.05+mat3(v0,0,.1*sin(iDate.w*4.),0,v0); 

	uv /= iResolution.y;
    uv-=.5;
	C = vec4(.3,.4,.5,1.0);

    //dots
    float dt=.005;
    for (int x=0;x<3;x++)
    {
        C+=vec4( length(b1[x].xy-uv)<dt );
        C+=vec4( length(b2[x].xy-uv)<dt );
    }

    //lines
    float lt=.01;
    vec2 l1,l2;
    for (float x=.0;x<1.;x+=.01)
    {
        l1=bez(b1,b2,x);
        l2=bez(b2,b1,x);
        vec2 cline=l1.xy*.5+l2.xy*.5;
        C+=vec4( length(cline-uv)<lt,0,0,1 );

        cline=l1.xy*.75+l2.xy*.25; 
        C+=vec4( length(cline-uv)<lt,0,0,1 );

        cline=l1.xy*.25+l2.xy*.75; 
        C+=vec4( length(cline-uv)<lt,0,0,1 );

        C+=vec4( 0,length(l1-uv)<lt,0,1 );
        C+=vec4( 0,length(l2-uv)<lt,0,1 );

    }
}
