#define _CelAmount 150
#define _Speed 0.08
#define _Seed 0.0
#define sqrtCellAmout sqrt(float(_CelAmount))


float rnd(vec2 xy)
{
    return fract(sin(dot(xy, vec2(12.9898+_Seed, 78.233+_Seed)))* (43758.5453+_Seed));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float dist, minDist = 99999.0;
    vec2 celPos, cellOffset;
    for(float i=0.0; i<sqrtCellAmout; i++)
    {
        for(float j=0.0; j<sqrtCellAmout; j++)
    	{
            celPos = vec2(i/sqrtCellAmout, j/sqrtCellAmout);
    		//cellOffset = vec2(rnd(celPos), rnd(vec2(celPos.x*dot(celPos.yx, vec2(284.4769, 7.95247)))));
    		cellOffset = vec2(rnd(celPos));
            cellOffset += vec2(tan((iTime+50.0)*_Speed*cellOffset.x), sin((iTime+50.0)*_Speed*cellOffset.y)*2.0);
            dist = distance(uv, celPos+cellOffset*0.07);

            if(dist < minDist)
                minDist = dist;
        }
    }
	fragColor = vec4(vec3(minDist*3.5)+vec3(0.5, 0.0, 0.04), 1.0);
}
