#define RADIUS 0.5
#define AB_SCALE 0.95

vec2 displace(vec2 uv, vec2 offset)
{   
    float d = smoothstep(0.1,2.0,texture(iChannel1, (uv*1.0 - vec2(iTime /4.0,0)) + offset).r) * 0.85;

    return vec2(d);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord/iResolution.xy;

    vec3 color = vec3(0);

    color += vec3(1, 0, 0)*texture(iChannel0, uv - displace(uv, vec2(0   , 0.02) * AB_SCALE)).r;
    color += vec3(0, 1, 0)*texture(iChannel0, uv - displace(uv, vec2(0.01, 0.01) * AB_SCALE)).g;
    color += vec3(0, 0, 1)*texture(iChannel0, uv - displace(uv, vec2(0.01, 0   ) * AB_SCALE)).b;

    /*
	color += vec3(1, 0, 0)*texture(iChannel0, uv).r;
    color += vec3(0, 1, 0)*texture(iChannel0, uv).g;
    color += vec3(0, 0, 1)*texture(iChannel0, uv).b;
	*/



    fragColor = vec4(color, 1.0);
}
