void mainImage( out vec4 O, vec2 U )
{
	U  = abs( U/iResolution.y -vec2(.64,.5) ) / vec2(1.3, 1);
    float a = min(U.x,U.y)*9.;

    O = vec4( a<1., a>.5 && a<1., a>.5, 1);
}









