void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
// Normalized pixel coordinates (from 0 to 1)
vec2 uv = fragCoord/iResolution.xy;

// Commented out colors to use textures instead
//vec4 newColorB = vec4(1.0,1.0,1.0,1.0);	//black
//vec4 newColorW = vec4(0.0,0.0,0.0,1.0);	//white

vec4 newColorB = texture(iChannel0, uv);	//abstract1 is used in place of black
vec4 newColorW = texture(iChannel1, uv);	//stars is used in place of white

float numRowsCols = 10.0;	//number of columns and rows

float temp1 = mod(floor(uv.x * numRowsCols), 2.0);	//veritcal divisions
float temp2 = mod(floor(uv.y * numRowsCols), 2.0);	//horizontal divisions


if( temp1 == 0.0)	//divides into columns
{
    fragColor = newColorB;	//paints every other column black
    if( temp2 == 0.0)	//divides black columns into rows
    {
	fragColor = newColorW;	//paints every other row white
	}
}
else
{
    fragColor = newColorW;	//paints every other column white
    if( temp2 == 0.0)	//divides white columns into rows
    {
	fragColor = newColorB;	//paints every other row black
    }
}
}
