// Sphere - Signed Distance Function
float sphere(vec3 p, float radius){
    return length(p)-radius;
}




// Locate objects
float map(vec3 p)
{    
    // Sphere radius
    float sphereSize = 0.5;

    // Transform coordinate space so spheres repeat
    vec3 q = fract(p) * 2.0 - 1.0;

     int tx = int(q.x);
    float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 
	fft *= 2.5;
    // Signed distance of sphere
    float s = sphere(q, sphereSize);

    float d = 0.1 * (cos(q.x*5.*fft) * cos(q.y*5.*fft) * cos(q.z*5.*fft) );
    //return s +wave;
    return s+d;
}


// Trace rays
float trace(vec3 origin, vec3 r) 
{
  float t = 0.0; // Distance Traveled
    for (int i = 0; i < 32; ++i) {
        vec3 p = origin + r * t;
        float d = map(p); // Locate object
        t += d * 0.5; // Step along the ray
    }
    return t; 
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3(3., 0.1, 0.4);

    uv = uv *2.-1.; // Remap the space to -1. to 1.
    uv.x *= iResolution.x/iResolution.y;

   	// Create ray to fire into scene
   	vec3 ray = normalize(vec3(uv, -4.0));

    // Create origin of scene
    vec3 origin = vec3(1., 1.,iTime);


    // Trace any objects in the scene
    float t = trace(origin, ray);

    // Generate fog based on distance from t
    float fog = 1. / (1.0 + sin(t) * cos(t) * 1.);

    // Final color with includes sdf + fog
    vec3 fc = vec3(fog);


    //fc /= cos(log(color*fft)*0.5);
    fc -= tan(color);
    // Output to screen
    fragColor = vec4(fc, tan(t));
}
