float num = 4.0;
float size = 2.0;

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
	vec2 uv = ceil((num * fragCoord.xy) * (vec2(iResolution.x / iResolution.y, 1.0) / iResolution.xy));

    vec3 math = vec3(mod(uv.x + uv.y, size));

	vec4 newColor = vec4(math, 1.0);

    fragColor = newColor;
}
