#define PI 3.14159265358979323

vec2 rotation(in float angle,in vec2 position,in vec2 center)
{
    //Function seen from https://www.shadertoy.com/view/XlsGWf
    float rot = radians(angle);
    mat2 rotation = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
    return vec2((position-center)*rotation);
}

vec2 scale(vec2 pos, float size, vec2 center)
{
 	return vec2((pos-center)*size);   
}

vec3 draw(vec2 uv, float time) {
    vec3 colors = vec3(0.);

    const int loops = 60;
    for (int i = 0; i < loops; i++) {
    vec3 rgb = vec3(uv.x,uv.y,float(i)/float(loops));
    vec2 uv2 = scale(uv-0.5,(pow(float(i),0.4))*1.5,vec2(0.))+0.5;
    float mask = clamp(step(uv2.x,1.)-step(uv2.x,0.),0.,1.)*(clamp(step(uv2.y,1.)-step(uv2.y,0.),0.,1.));
    vec2 positions = (vec2(sin(time+ (float(i)*1.48)),cos(time+float(i)*2.5))/2.)+0.5;
    mask = 1.-((length(uv-vec2(positions)))* ((2.*float(i))+4.4));
    mask = clamp(mask,0.,1.)*10.;
    colors += vec3(mix(vec3(0.),rgb,vec3(mask)));
    }
    colors = clamp(colors,0.,1.);

    return vec3(colors);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time=iTime*0.9;
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 suv = vec2(((uv.x-0.5)*(iResolution.x / iResolution.y))+0.5,uv.y);
	fragColor = vec4((draw(suv,time+sin(uv.y*4.))+draw(uv,time+sin(uv.x*6.))),1.0);
}

float rand(vec2 co){
    return sin(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 mainSound( in int samp,float t)
{
    float time = t*0.4;
    float a = sin(((sin(time*4.)*100.))+time);
    float b = sin((time*800.)+sin(time*700.));
    float c = rand(vec2(time*0.00004));
    float output1 = ((a*b)+c)/3.;
    return vec2( output1 );
}
