void mainImage( out vec4 FragColor, in vec2 FragCoord ) 
{
    vec2 mitt = vec2 (iResolution.x/2.+cos(iTime)*iResolution.y/3.,iResolution.y/3.-sin(iTime)*iResolution.y/3.);
    vec2 mitt2 = vec2 (iResolution.x/3.+cos(iTime+2.1)*iResolution.y/3.,2.*iResolution.y/3.+sin(iTime+2.1)*iResolution.y/3.);
    vec2 mitt3 = vec2 (2.*iResolution.x/3.+cos(iTime+4.2)*iResolution.y/3.,2.*iResolution.y/3.+sin(iTime+4.2)*iResolution.y/3.);
	vec2 punkt = gl_FragCoord.xy;
    float radie=length(punkt-mitt)+iTime*60.;
    float radie2=length(punkt-mitt2)-iTime*80.;
    float radie3=length(punkt-mitt3)-iTime*70.	;
    float kontrast = 0.;
    float kontrast2 = 0.;
    float kontrast3 = 0.;
    if  (mod((gl_FragCoord.y+iTime*4.5+sin(iTime*1.1)*3.)*0.0651,4.)>1.)
        kontrast = 1.;
    if  (mod((gl_FragCoord.x+iTime*5.5+sin(iTime*1.6)*3.)*0.052,4.)>1.)
        kontrast = abs(-1.+kontrast);
    if  (mod((gl_FragCoord.y-iTime*6.5+cos(iTime*1.3)*3.)*0.0453+0.33,4.)>1.)
        kontrast2 = 1.;
    if  (mod((gl_FragCoord.x-iTime*7.+sin(iTime*1.5)*3.)*0.0454+0.33,4.)>1.)
        kontrast2 = abs(-1.+kontrast2);
	if  (mod((gl_FragCoord.y+iTime*6.+cos(iTime*1.4)*3.)*0.0655+0.67,4.)>1.)
        kontrast3 = 1.;
    if  (mod((gl_FragCoord.x-iTime*5.+cos(iTime*1.2)*3.)*0.0556+0.67,4.)>1.)
        kontrast3 = abs(-1.+kontrast3);

    FragColor = vec4(
        (abs(sin(radie*0.062)*0.3)+kontrast*0.2+abs(sin(radie3*0.06)*0.1))*0.601,
        (abs(sin(radie2*0.058)*0.2)+kontrast2*0.25+abs(sin(radie3*0.06)*0.1))*0.501,
        (abs(sin(radie3*0.06)*0.4)+kontrast3*0.4)*1.6001,
        1);
	}
