#define PI 3.14159265359
#define TPI 6.28318530718
#define minF(a) min(fract(a), fract(1.-a))
#define sat(a) clamp(a, 0., 1.)


//horizontal ramp
float gr_x(vec2 xy, float cycles, float speed)
{ 
    return smoothstep(0.0,1.0,fract(xy.x*cycles-iTime*speed));
}

//vertical ramp
float gr_y(vec2 xy, float cycles, float speed)
{ 
    return smoothstep(0.0,1.0,fract(xy.y*cycles-iTime*speed));
}

//circular ramp
float gr_x_rad(vec2 xy, float cycles, float speed)
{ 
    return smoothstep(0.0,1.0,fract(atan(xy.y, xy.x)*cycles/TPI-iTime*speed));
}

//radial ramp
float gr_y_rad(vec2 xy, float cycles, float speed)
{ 
    return smoothstep(0.0,1.0,fract(length(xy)*cycles/TPI-iTime*speed));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.* fragCoord.xy - iResolution.xy)/iResolution.y;
    float c;


    c = gr_x(uv, 5., .25);
   // c = gr_y(uv, 5., .25);
   // c= gr_x_rad(uv, 5., .25);
    c= gr_y_rad(uv, 25., .25);

	fragColor = vec4(vec3(c),1.0);
}
