void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //Variables
    float rows = 5.0;
    float cols = 5.0;
    //Colors
    vec4 black = vec4(0,0,0,1);
    vec4 white = vec4(1,1,1,0);

    //Create Rows and Columns
    float x = floor(uv.x * cols);
    float y = floor(uv.y * rows);
    float total = (x + y);

    //Color Tiles Appropriately
    if (mod(total, 2.0) == 0.0)
        fragColor = black;
    else
        fragColor = white;
}
