//All my variables
vec2 Location = vec2(.5,0.5);
float radius = 0.1;
vec3 BgColor = vec3(1.0,0.0,0.0);
vec3 FgColor = vec3(1.0,0.0,1.0);

float frequency = 3.0;
float amplitude = 0.2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    //creates the aspect ration needed to adjust the circle
  float aspectRatio = iResolution.y / iResolution.x;  

  vec2 uv = fragCoord.xy / iResolution.xy;

    //adjusts the circle to make it round
  uv.y *= aspectRatio;

     vec4 text = texture(iChannel0, uv);

    //adjusts the offset from the aspect ratio
  Location.y *= aspectRatio;

    //moves the circle up and down
  Location.y = Location.y + sin(iTime * frequency) * amplitude;

    //adjust the background gradient to move over time
  BgColor.r*=  sin(iTime * 5.0);
  FgColor*=  sin(iTime * 3.0) + 1.5;

    //changes the radius of the circle over time
    //this gives the effect of the circle coming
    //closer and moving away
  radius *= sin(iTime * 1.5);

    //draws the circle
  if(length(Location - uv) < radius)
  	fragColor = vec4(FgColor,1.0);
    //draws the gradient 
  else{ 
  	fragColor = vec4(BgColor,1.0);   
 	fragColor.rgb *= uv.x;
  }
}
