float checkSize = 25.0;
float spotRadius = 256.0;
vec2 center;
float sinVal = 0.0;
vec2 offset = vec2( 0.0, 0.0 );

vec2 getGridReference( vec2 coord )
{

    return vec2( coord.x / checkSize, coord.y / checkSize );

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   	spotRadius = 64.0 + pow( abs( 128.0 * cos( iTime * 0.35 ) ), 1.3 );

    vec2 uv = fragCoord.xy / iResolution.xy;
    offset.x += ( iTime * 60.0 ) * ( ( iMouse.x / iResolution.x ) * 3.0 );
    offset.y += ( iTime * 60.0 ) * ( ( iMouse.y / iResolution.y ) * 3.0 );

    vec2 gridRef = getGridReference( fragCoord.xy + offset );

    int x = int(round(gridRef.x));
    int y = int(round(gridRef.y));
    center = iResolution.xy / 2.0;
    float divAmt = 360.0 / 720.0;
    float dist = 1.0 - ( round( length( fragCoord.xy - iMouse.xy ) / divAmt ) / ( spotRadius / divAmt )  );
    vec4 col = vec4( uv.x, dist, uv.y, 1.0 );
    if( ( x % 2 == 0 && y % 2 == 0 ) || ( x % 2 == 1 && y % 2 == 1 ) ) col.xyz *= vec3( 1.0 - dist, max( dist, 1.0 ), ( dist + 1.0 ) / 2.0 );
	else col.xyz *= vec3( 0.2 * dist, 0.4 - dist, 0.2 * dist );

    fragColor = col;

}
