void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float textureScale = 4.0;

    vec2 size = vec2( 0.0675, 0.1 );
    vec2 pos = iMouse.xy / iResolution.xy - size / 2.0;

    if ( uv.x >= pos.x && uv.x <= pos.x + size.x && uv.y >= pos.y && uv.y <= pos.y + size.y ){
    	fragColor = texture( iChannel0, uv * textureScale - pos * textureScale );
    }
}
