#define THRESH 0.001
#define LBLUE vec3(128.0 / 255.0, 188.0 / 255.0, 248.0 / 255.0)
#define DBLUE vec3(4.0 / 255.0, 52.0 / 255.0, 196.0 / 255.0)
#define CLOUD vec3(251.0 / 255.0, 251.0 / 255.0, 253.0 / 255.0)

float metaball(in vec2 location, in vec2 fragCoord) {
    return 1.0 / (
        pow(location.x - fragCoord.x, 2.0) +
        pow(location.y - fragCoord.y, 2.0));
}

float tri(in float x) {
    return abs(fract(x) * 2.0 - 1.0);
}

vec2 ballPos(in float time) {
    return vec2(
        tri(time * 0.12) * iResolution.x,
        abs(sin(time * 2.5)) * iResolution.y * 0.9);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 pos1, pos2, pos3, pos4, pos5;
    pos1 = ballPos(iTime);
    pos2 = ballPos(iTime + 8.0);
    pos3 = ballPos(iTime * 0.9 + 13.5);
    pos4 = ballPos(iTime * 1.04 + 2.5);
    pos5 = ballPos(iTime * 0.97 + 8.5);
    float sum = metaball(pos1, fragCoord) +
                metaball(pos2, fragCoord) +
                metaball(pos3, fragCoord) + 
                metaball(pos4, fragCoord) +
                metaball(pos5, fragCoord);
    vec3 skyCol = mix(LBLUE, DBLUE, fragCoord.y / iResolution.y);
	fragColor = vec4(mix(skyCol, vec3(0.0, 0.0, 0.0), THRESH - sum / THRESH), 1.0);
}
