#define STEP .005
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 decal = texture(iChannel1,uv).xy - vec2(.5);
    uv = uv + decal * STEP;
	fragColor = (texture(iChannel0,uv + vec2(1,1) * STEP) +
        texture(iChannel0,uv + vec2(-1,1) * STEP) +
        texture(iChannel0,uv + vec2(1,-1) * STEP) +
        texture(iChannel0,uv + vec2(-1,-1) * STEP))/4. ;
}
