/*
A BRDF for snow. The biggest differences to a lambertian surface are the soft forward reflection peak and subtle limb darkening.
camera controls via mouse + shift key

BRDF code in the 'Common' tab.
*/

//#define COMPARE_WITH_LAMBERT


///////////////////////////////////////////////////////////////////////////
//=======================================================================//

vec3 GammaEncode(vec3 x) {return pow(x, vec3(1.0 / 2.2));}   

struct Cam
{
	vec3 Front, Right, Up;
	float Aspect;
	float AxisLen;	
};

Cam NewCam(vec2 ang, float fov, float aspect)
{
    Cam cam;

    float sinPhi   = sin(ang.x);
    float cosPhi   = cos(ang.x);
    float sinTheta = sin(ang.y);
    float cosTheta = cos(ang.y);    

    cam.Front = vec3(cosPhi * cosTheta, 
                              sinTheta, 
                     sinPhi * cosTheta);

    cam.Right = vec3(-sinPhi, 0.0, cosPhi);
    cam.Up    = cross(cam.Right, cam.Front);

    cam.Aspect = aspect;
    cam.AxisLen = aspect * tan(Pi05 - fov * 0.5);

    return cam;
}

// tc [-1..1]
vec3 NewRay(Cam cam, vec2 tc)
{
    tc.x *= cam.Aspect;

    vec3 imgPos = cam.Front * cam.AxisLen + (cam.Right * tc.x + cam.Up * tc.y);

    vec3 dir = normalize(imgPos);

    return dir;
}


float map(vec3 x);

vec3 GradientCD(vec3 x, float o)
{
	float xn = map(vec3(x.x - o, x.y, x.z));
	float xp = map(vec3(x.x + o, x.y, x.z));

	float yn = map(vec3(x.x, x.y - o, x.z));
	float yp = map(vec3(x.x, x.y + o, x.z));

	float zn = map(vec3(x.x, x.y, x.z - o));
	float zp = map(vec3(x.x, x.y, x.z + o));

	vec3 g = vec3(xp - xn, yp - yn, zp - zn) / o * 0.5;

	return g;
}

vec3 GradientCD(vec3 x)
{
    return GradientCD(x, 0.0001);
}

vec3 GradientFD(vec3 x, float o)
{
	x -= o * 0.5;
	float c = map(x);
	// c = 0.0;

	float xp = map(vec3(x.x + o, x.y, x.z));
	float yp = map(vec3(x.x, x.y + o, x.z));
	float zp = map(vec3(x.x, x.y, x.z + o));

	vec3 g = vec3(xp - c, yp - c, zp - c) / o;

	return g;
}

vec3 GradientFD(vec3 x)
{
    return GradientFD(x, 0.0001);
}


bool ImplicitRC(
float funcScale,
vec3 x, 
vec3 dir, 
float linCount,
float binCount,
float stepSizeAdd,
out float t0, out float tm, out float t1)
{
	bool hit = false;

    t0 = tm = t1 = 0.0;

    float stepSize = abs(map(x)) + stepSizeAdd;

	for(float i = 0.0; i < linCount; ++i)// linear search
	{	
		t1 += stepSize;

        vec3 p = x + dir * t1;

        float f = map(p) * funcScale;

        stepSize = abs(f) + stepSizeAdd;

        hit = f < 0.0;

        if(hit) {i=linCount;}
        //if(hit) {break;}        
        else {t0 = t1;}
	}


	if(hit)
    {
        #if 1
        for(float i = 0.0; i < binCount; ++i)// binary search
		{
			tm = (t0 + t1) * 0.5;

            vec3 p = x + dir * tm;

			if(map(p) * funcScale < 0.0)
			t1 = tm;
			else
			t0 = tm;
		}

        vec3 p0 = x + dir * t0;
        vec3 p1 = x + dir * t1;

        float f0 = map(p0) * funcScale;
        float f1 = map(p1) * funcScale;

        float div = f0 - f1;

        tm = mix(t0, t1, abs(div) > 1e-16 ? f0 / div : 0.5);// linear interpolation
        #else
        tm = (t0 + t1) * 0.5;
        #endif

        return true;
    }
    else
    return false;
}




float Root4(float x)
{
    return rsqrt(rsqrt(x));
}

vec2 CmplxMul(vec2 c0, vec2 c1)
{
	return vec2(c0.x*c1.x - c0.y*c1.y, c0.y*c1.x + c0.x*c1.y);
}

vec2 CmplxMul(vec2 c0, float ang)
{
	vec2 c1 = vec2(cos(ang), sin(ang));

	return vec2(c0.x*c1.x - c0.y*c1.y, c0.y*c1.x + c0.x*c1.y);
}

float map(vec3 x)
{
    float r = length(x) - 1.0;
    //return r;

    float t0 = 0.0;
    float t1 = 0.0;
    float t2 = 0.0;

    #if 1
    float t = iTime * 0.6;
    t0 = t * 1.23;
    t1 = t * 1.37;
    t2 = t * 2.11;
    x.xz = CmplxMul(x.xz, t * 0.21);
    #endif

    float o = 1.0;
    float s = 11.0;
    o *= cos(x.x * s + t0);
    o *= sin(x.z * s + t1);
    o *= cos(x.y * s + t2);

    o = o*0.5+0.5;

    o = 1.0 - rsqrt(rsqrt(1.0 - o * 0.95));

    r -= o * 0.2;

    return r;
}


vec3 EvalSceneCol(
    vec3 rp, Cam cam, vec2 uv0, bool right)
{
    vec3 col = vec3(0.5);
    vec2 PixelCount = iResolution.xy;// * vec2(0.5, 1.0);

    vec2 tex = uv0.xy / PixelCount;
    vec2 tex21 = tex * 2.0 - vec2(1.0);

    #ifdef COMPARE_WITH_LAMBERT
    tex21.x = abs(tex21.x);
    #endif

    vec3 rd = NewRay(cam, tex21);

    vec2 t; vec3 n; vec3 a;
    float t0, tm, t1;
    bool hit = ImplicitRC(1.0, rp, rd, 50.0, 5.0, 0.005, /*out*/ t0, tm, t1);

    if(hit)
    {
        vec3 p = rp + rd * tm; 

       	n = normalize(GradientCD(p));

        vec3 amb = vec3(0.0, 0.4, 1.0) * 0.24*0.0;

        vec3 L = vec3(0.0, 1.0, 0.0);
        vec3 V = -rd;

        float NdL = clamp01(dot(n, L));
    	float NdV = clamp01(dot(n, V));
    	float VdL =         dot(V, L) ;

        #ifdef COMPARE_WITH_LAMBERT
        float brdf = right ? SnowBRDF(NdL, NdV, VdL) : SSAlbedo_to_Albedo(0.995) * RcpPi;
		#else
        float brdf = SnowBRDF(NdL, NdV, VdL);
        #endif
        col = vec3(brdf * NdL * 3.5) + amb;

        //col = n*0.5+0.5;
    }

    return col;
}


void mainImage( out vec4 outCol, in vec2 uv0 )
{
    vec3 col = vec3(0.0);

    vec2 uv = uv0.xy - 0.5;

    vec2 PixelCount = iResolution.xy;// * vec2(0.5, 1.0);

    bool right = uv.x > PixelCount.x * 0.5;
    //if(right) uv.x -= PixelCount.x;


    vec4 mouseAccu = texelFetch(iChannel0, ivec2(1, 0), 0); 
    vec4 wasdAccu  = texelFetch(iChannel0, ivec2(2, 0), 0); 

    vec2 lightAng = vec2(Pi * 0.0, Pi * 0.5);
    lightAng.x += (wasdAccu.y - wasdAccu.w) * 0.06; 
    lightAng.y += (wasdAccu.x - wasdAccu.z) * 0.04;    


    vec2 ang = vec2(-0.25 * Pi, -Pi * 0.2);
    ang += mouseAccu.xy * 0.008;

    Cam cam = NewCam(ang, Pi * 0.5, PixelCount.x / PixelCount.y);
    vec3 cpos = -cam.Front * 2.0;
    cpos = -cam.Front * (exp2(1.2 + mouseAccu.w * 0.02));


	#if 1
    col = vec3(EvalSceneCol(cpos, cam, uv, right));  
    #else
    col  = EvalSceneCol(cpos, cam, uv + vec2(0.3, 0.1), right);
    col += EvalSceneCol(cpos, cam, uv + vec2(0.9, 0.3), right);
    col += EvalSceneCol(cpos, cam, uv + vec2(0.5, 0.5), right);
    col += EvalSceneCol(cpos, cam, uv + vec2(0.1, 0.7), right);
    col += EvalSceneCol(cpos, cam, uv + vec2(0.7, 0.9), right);   
    col *= 0.2; 
    #endif

    #if 1
    vec2 s = abs(uv0/iResolution.xy*2.0-1.0);
    s.x = 1.0-Pow3(s.x);    s.y = 1.0-Pow3(s.y);
    col *= mix(1.0, 0.4, Pow2(1.0-sqrt(s.x*s.y)));
    #endif

    //if(col.x > 1.0) col = vec3(1.0, 0.0, 1.0);
    //if(col.x >

	outCol = vec4(GammaEncode(clamp01(col)), 1.0);
}

// persistent state stuff

void mainImage( out vec4 col, in vec2 uv0 )
{  
    col = vec4(0.0);

    vec2 uv = uv0 - 0.5;


    vec4 iMouseLast     = texelFetch(iChannel0, ivec2(0, 0), 0);
    vec4 iMouseAccuLast = texelFetch(iChannel0, ivec2(1, 0), 0);
    vec4 wasdAccuLast   = texelFetch(iChannel0, ivec2(2, 0), 0);

    bool shift = texelFetch(iChannel1, ivec2(16, 0), 0).x != 0.0;

    float kW = texelFetch(iChannel1, ivec2(0x57, 0), 0).x;
    float kA = texelFetch(iChannel1, ivec2(0x41, 0), 0).x;
    float kS = texelFetch(iChannel1, ivec2(0x53, 0), 0).x;
    float kD = texelFetch(iChannel1, ivec2(0x44, 0), 0).x;

    vec4 wasdAccu = wasdAccuLast + vec4(kW, kA, kS, kD);

    vec2 mouseDelta = iMouse.xy - iMouseLast.xy;

    bool cond0 = iMouse.z > 0.0 && iMouseLast.z > 0.0;
    vec2 mouseDelta2 = cond0 && !shift ? mouseDelta.xy : vec2(0.0);
    vec2 mouseDelta3 = cond0 &&  shift ? mouseDelta.xy : vec2(0.0);

    vec2 iMouseAccu1 = iMouseAccuLast.xy + mouseDelta2;
    vec2 iMouseAccu2 = iMouseAccuLast.zw + mouseDelta3;

    if(uv.x == 0.0 && uv.y == 0.0) col = iMouse;  
    if(uv.x == 1.0 && uv.y == 0.0) col = vec4(iMouseAccu1, iMouseAccu2);
    if(uv.x == 2.0 && uv.y == 0.0) col = wasdAccu;
}

