/*
A BRDF for snow. The biggest differences to a lambertian surface are the soft forward reflection peak and subtle limb darkening.

Hapke BRDF:
Hapke, Bruce. "Bidirectional reflectance spectroscopy: 1. Theory." 	Journal of Geophysical Research: Solid Earth 86.B4 (1981): 3039-3054.
shadow hiding term:
Hapke, Bruce. "Bidirectional reflectance spectroscopy: 4. The extinction coefficient and the opposition effect."	Icarus 67.2 (1986): 264-280.

Parameter fit for snow:
Domingue, Deborah, Beth Hartman, and Anne Verbiscer. "The scattering properties of natural terrestrial snows versus icy satellite surfaces." (1997).

(all accessible via https://sci-hub.tw/)
*/

// no shadow hiding term:
#define USE_MINIMALIST_BRDF


//-----------------------------------------------------------------------------------------------------------------------//

#define Frame float(iFrame)
#define Time iTime
#define OUT

#define rsqrt inversesqrt
#define clamp01(x) clamp(x, 0.0, 1.0)
#define If(cond, resT, resF) mix(resF, resT, cond)


const float Pi = 3.14159265359;
const float Pi2 = Pi * 2.0;
const float Pi05 = Pi * 0.5;

const float RcpPi  = 1.0 / (1.0 * Pi);
const float RcpPi2 = 1.0 / (2.0 * Pi);
const float RcpPi4 = 1.0 / (4.0 * Pi);

float Pow2(float x) {return x*x;}
float Pow3(float x) {return x*x*x;}
float Pow4(float x) {return Pow2(Pow2(x));}


// pow-like + inverse; build by rescaling [exp(x * s) + exp(-x * s) - 2]  
float PowL_Exp(float x, float s)
{
    return exp(s - s * x) * Pow2(exp(s * x) - 1.0) / Pow2(exp(s) - 1.0);
}

float PowL_ExpInv(float x, float s)
{
    float expS = exp(s);
    float exp2S = expS * expS;

    float t0 = 2.0 * expS + x - 2.0 * expS * x + exp2S * x;

    float t1 = sqrt((-2.0 * expS * (x - 2.0) + x + exp2S * x) * x) * (expS - 1.0);

    return log((t0 + t1) * exp(-s) * 0.5) / s;
}


/////////////////////////////////////////////////////////////////////////////////////////////////////
//=================================================================================================//

//------------------------------------//
// single scattering albedo <-> (spherical) albedo mappings
// assume first order approx of H (by Hapke) and iso scattering
// usefull for matching the appearance of Hapke to Lambert and vice versa

const float HapkeMaxAlbedo = (11.0 + 28.0 * log(2.0)) / 30.0;// 1.0136 (> 1 due to error in H approx)

// SSAlbedo_to_Albedo has no closed form (plane albedo has but the (hemi-)spherical one apparently doesnt)
// this approx has mean-absolute-error 0.000971859
float SSAlbedo_to_Albedo(float a_ss)
{
    return (1.0 - PowL_ExpInv(1.0 - a_ss, 4.776)) * HapkeMaxAlbedo;
}

float Albedo_to_SSAlbedo(float a)
{
    return 1.0 - PowL_Exp(a * (-1.0 / HapkeMaxAlbedo) + 1.0, 4.776);
}
//------------------------------------//


float HapkeBRDF(float mu, float mu0, float a_ss)
{
    float s = sqrt(1.0 - a_ss);

    //vec3 H  = (1.0 + 2.0 * mu ) / (1.0 + 2.0 * mu  * s);
    //vec3 H0 = (1.0 + 2.0 * mu0) / (1.0 + 2.0 * mu0 * s);            

    float HH0 = ((1.0 + 2.0 * mu    ) * (1.0 + 2.0 * mu0    )) / 
                ((1.0 + 2.0 * mu * s) * (1.0 + 2.0 * mu0 * s));           

    return a_ss * HH0 / (mu + mu0) * RcpPi4;
}

float HapkeBRDF(float mu, float mu0, float a_ss, float phFunc)
{
    float s = sqrt(1.0 - a_ss);            

    float HH0 = ((1.0 + 2.0 * mu    ) * (1.0 + 2.0 * mu0    )) / 
                ((1.0 + 2.0 * mu * s) * (1.0 + 2.0 * mu0 * s));

    return a_ss * (HH0 - 1.0 + phFunc) / (mu + mu0) * RcpPi4;
}

float ShadowHiding(float cosTheta, float h, float B0)
{
    float tan05 = (1.0 + cosTheta) * rsqrt(1.0 - cosTheta*cosTheta);

    return 1.0 + B0 / (1.0 + tan05 / h);
}


float HenyeyGreensteinPhF(float cosTheta, float g)
{
	float t0 = 1.0 - g*g;
	float t1 = 1.0 + g*g - 2.0 * g * cosTheta;

	// return t0 * rsqrt(max(0.0, t1*t1*t1));// nan if ct&&g==-1 or ct&&g==1
	return t0 * rsqrt(max(1.0e-37, t1*t1*t1));
}

float SchlickPhF(float cosTheta, float k)
{	
	float t0 = 1.0 - k*k;
	float t1 = 1.0 - k * cosTheta;

	//return t0 / (t1 * t1);// nan if ct&&g==-1 or ct&&g==1
	return t0 / max(1.0e-37, t1 * t1);    
}


// approx. minimizes RMS error between HG and Schlick (not used at runtime here)
float GtoSchlickK(float g)
{	
    const float a = -0.132207;
    const float b = -0.735586;
    const float c = 0.5 + a;

    float k = g * (1.5 + g*g * (a + (b + c * abs(g)) * abs(g)));

    return k;
}


#ifdef USE_MINIMALIST_BRDF
float SnowBRDF(float NdL, float NdV, float VdL)
{   
    float phF = SchlickPhF(-VdL, 0.702);
          //phF = HenyeyGreensteinPhF(-VdL, 0.503);

    return HapkeBRDF(NdV, NdL, 0.995, phF);
}
#else
float SnowBRDF(float NdL, float NdV, float VdL)
{    
    float sh = ShadowHiding(-VdL, 0.995, 1.0);

    float phF = SchlickPhF(-VdL, 0.634) * sh;
          //phF = HenyeyGreensteinPhF(-VdL, 0.445408) * sh;

    return HapkeBRDF(NdV, NdL, 0.995, phF);
}
#endif
