const float horizon = 0.3;
bool isTerrain(vec2 uv)
{
    float o = 0.2*iTime + uv.x;
    float a = 0.01;//*sin(1.0-uv.x);
    return (uv.y + a*sin(50.*o) ) < horizon;
}

vec4 shadeTerrain(vec2 uv)
{
    float dist = horizon - uv.y;

    return vec4(.1-dist, 0.4-dist, 0.4-dist, 0.);
}

vec4 shadeSky(vec2 uv)
{
    float dist = uv.y - horizon;
    return vec4(.3-dist, .6-dist, 0.9-dist, 1.);
}

bool isSun(vec2 uv, float time, out float dist)
{
    vec2 origin = vec2(-0.5 + sin(time+uv.x*0.5)*0.5, -0.25 + cos(time+uv.y*0.5)*0.5);
    return length(uv+origin) < 0.05;
}

bool isBoat(vec2 uv) 
{
    return false;
    return length(uv+vec2(-0.5, -0.5)) < .05;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float dist = 0.;
    if (isTerrain(uv))
    {
		fragColor = shadeTerrain(uv);
    } else if (isBoat(uv)) 
    {
        fragColor = vec4(1.);
    } else if (isSun(uv, iTime*0.05, dist))
    {
        fragColor = vec4(2.-uv.y, uv.y, 0., 1.0);
    }
    else
    {
        fragColor = shadeSky(uv);
    }


}
