// WaveText - 2018 by JT (https://www.shadertoy.com/view/4tSfDV)
// Turn text into waves - and reconstruct it by simply negating simulation time-step.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 p = ivec2(fragCoord);
    fragColor = vec4(0);
    fragColor.z = texelFetch(iChannel0, p, 0).z; // pressure
    fragColor.xy = texelFetch(iChannel1, p, 0).xy; // velocity
    //fragColor.xy = texelFetch(iChannel2, p, 0).xy; // acceleration
}

// WaveText - 2018 by JT (https://www.shadertoy.com/view/4tSfDV)
// Turn text into waves - and reconstruct it by simply negating simulation time-step.

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    ivec2 p = ivec2(fragCoord);
    ivec2 s = ivec2(textureSize(iChannel0, 0));
    ivec2 r = (p + ivec2(1,0)) % s;
    ivec2 u = (p + ivec2(0,1)) % s;
    float k = 1.0;
    fragColor = vec4(0);
    if(iFrame > 0)
    {
        // update acceleration (pressure-gradient)
    	fragColor.x = (texelFetch(iChannel0, p, 0).z - texelFetch(iChannel0, r, 0).z) * k;
    	fragColor.y = (texelFetch(iChannel0, p, 0).z - texelFetch(iChannel0, u, 0).z) * k;
    }
}

// WaveText - 2018 by JT (https://www.shadertoy.com/view/4tSfDV)
// Turn text into waves - and reconstruct it by simply negating simulation time-step.

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    ivec2 p = ivec2(fragCoord);
    ivec2 s = ivec2(textureSize(iChannel0, 0));
    //float dt = (mod(iTime, 10.0) < 5.0 ? +1.0 : -1.0) * 0.5;//iTimeDelta;
    float dt = (mod(iTime, 20.0) < 10.0 ? +1.0 : -1.0) * 0.1;//iTimeDelta;
    fragColor = vec4(0);
    if(iFrame > 0)
    {
        // update velocity (add acceleration)
    	fragColor = texelFetch(iChannel1, p, 0) + texelFetch(iChannel0, p, 0) * dt;
    }
}

