    //float dt = (mod(iTime, 10.0) < 5.0 ? +1.0 : -1.0) * 0.5;//iTimeDelta;
    float dt = (mod(iTime, 20.0) < 10.0 ? +1.0 : -1.0) * 0.1;//iTimeDelta;
    fragColor = vec4(0);
    if(iFrame > 0)
    {
        // update pressure (add velocity)
        fragColor = texelFetch(iChannel1, p, 0);
    	fragColor.z += (texelFetch(iChannel0, l, 0).x - texelFetch(iChannel0, p, 0).x) * dt;
    	fragColor.z += (texelFetch(iChannel0, d, 0).y - texelFetch(iChannel0, p, 0).y) * dt;
    }
    else
    {
        vec2 q = 2.0 * vec2(p) / vec2(s) - 1.0;
