void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = 2.0*fragCoord/iResolution.xy - 1.0;
    p.x *= iResolution.x/iResolution.y;
    float v = 0.0;
    float t = mod(float(iTime), 94.25);
    for (float i = 1.0; i <= 50.0; i++) {
        vec2 center = i * 0.018 * vec2(cos(i*t*0.066666), sin(i*t*0.066666));
        v += 1.0-smoothstep(0.029, 0.031, length(center-p));
    }
    vec4 bg_color = mix(vec4(255., 170., 166., 256.)/256.,
    					vec4(168., 208., 230., 256.)/256.,
                        abs(t / 47.125 - 1.0));
    fragColor = mix(bg_color, vec4(0.0, 0.0, 0.0, 1.0), v);
}
