void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float f = uv.y;
    f = log2(f);
    f = abs(f);
    f = floor(f);
    f = pow(2.,f) * 2.;
    f = f * (uv.x + 1.);

    f += sin(iTime); //comment for a still image

    f = fract(f);
    f = round(f);

	fragColor = vec4(f);
}
