#define PI 3.1415926535897932384626
#define F 16.0
#define time iTime
#define cc(x,a,b,c,d) (a+b*cos(2.*PI*(c*x+d)))
#define hash21(p) (-1.+2.*fract(sin(dot(p, vec2(127.1,311.7)))*43758.5453123))
vec2 hash22(vec2 p)
{
    p = mat2(127.1, 269.3, 311.7, 183.3)*p;
    return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r = iResolution.xy;
	vec2 p = (2.*fragCoord.xy - r)/r.y*.4;
    vec2 m = (2.*iMouse.xy - r)/r.y;
    p *= 0.5 + 0.5*hash21(p);
    float c = 0.5+0.5*sin(2.0*PI*F*length(p-m) + time);
	fragColor = cc(c,.5,.5,1.,vec4(.1,.2,.3,1.));
}
