vec2 customUV(in vec2 fragCoord){
 vec2 uv = fragCoord.xy / iResolution.xy;
    uv = mix(uv, tan(uv * 14.0) / 14.0, 0.3);

    return uv;
}

float getSpec(vec2 uv)
{
    vec2 mousePosition = iMouse.xy / iResolution.xy;
	return max(smoothstep(0.6,0.72,(1.0 - distance(uv, mousePosition)) - 0.3) - 0.3, 0.0) * 1.5;   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = customUV(fragCoord); 

	fragColor = vec4(vec3(getSpec(uv)) + 0.4 * (1.0 - distance(uv, vec2(0.5))),1.0);
}
