vec2 withIn(vec2 uv,vec2 start,vec2 end)
{
	return (uv-start)/(end-start);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;

    float angle = 60.0;
    mat2x2 m = mat2x2(cos(angle/180.*3.14),sin(angle/180.*3.14),
                      -sin(angle/180.*3.14),cos(angle/180.*3.14));
    uv = m*uv;
    vec2 uv2 = withIn(uv,vec2(-0.25,-0.25),vec2(0.25,0.25));
    uv2 -= 0.5;
    uv2.y -= pow(abs(uv2.x),2.0/3.0)*0.6;

    float ingridxB = step(uv2.x,0.5);
    float ingridxA = step(-0.5,uv2.x);
    float ingridyB = step(uv2.y,0.5);
    float ingridyA = step(-0.5,uv2.y);
    float ingridx = smoothstep(1.0,2.0,ingridxA+ingridxB);
    float ingridy = smoothstep(1.0,2.0,ingridyA+ingridyB);
    float ingrid = ingridx*ingridy;

    vec3 col = vec3(ingrid,ingrid,ingrid);
    vec3 heartColor = vec3(1.0,0.0,0.0);

    float d = length(uv2);
    float s = smoothstep(0.0,0.5,d);
    float a = smoothstep(0.3,0.29,d);

    col = mix(col,heartColor, a);

    // Output to screen
    fragColor = vec4(col,1.0);
}
