#define FUNCTION(x) sin(exp(x))
#define EPS 0.05

#define DOMAIN_LOW -1.0
#define DOMAIN_HIGH 2.54

#define RANGE_LOW -1.1
#define RANGE_HIGH 1.1

#define FILLED vec4(0.4, 0.6, 1.0, 1.0)
#define BLANK vec4(1.0, 1.0, 1.0, 1.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    if (abs(RANGE_LOW + (RANGE_HIGH - RANGE_LOW)*uv.y - 
            FUNCTION(DOMAIN_LOW + (DOMAIN_HIGH - DOMAIN_LOW)*uv.x)) < EPS) {
        fragColor = FILLED;
    } else {
        fragColor = BLANK;
    }   
}
