//Heavily inspired by https://www.shadertoy.com/view/4d23Rm


const float thickness = 1.0;
const float scale = 0.09;

//the function to plot
float f(float x){
    return x*sin(3.0*(x + 0.5*-iTime*sign(x)));
}

//apply scale
float f1(float x){
    return scale * f(x / scale);
}

vec2 gradient(float x){
    float eps = 0.007;
    vec2 nor = vec2(
        2.0* eps,
        f1(x+eps) - f1(x-eps));
    return normalize(nor);
}

//http://imajeenyus.com/mathematics/20121112_distance_estimates/point_to_function.pdf
float distanceToFunction(vec2 p){
    float num = abs(p.y - f1(p.x));
    vec2 grad = gradient(p.x);
    float fpx = grad.y/grad.x;
    float den = sqrt((fpx*fpx)+1.0);
    return num/ den;
}


// @return	Alpha for a pixel which is dist away for a given max allowed radius.
float coverage(float dist, float radius){
	radius *= 0.5;
	return 1.0 - smoothstep(floor(radius), radius + 0.01, dist /4.0 * iResolution.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    //centered coordinates
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
	uv.x *= iResolution.x / iResolution.y;

    //how far is our point from the function
	//float distY = abs(y - uv.y);
    float dist = distanceToFunction(uv);

    vec3 lineColor = vec3(1.0,1.0,1.0);
	vec4 plot = vec4(lineColor, coverage(dist, thickness));

	vec4 backgroundColor = vec4(0.0,0.0,0.0,0.0);
	fragColor = mix(backgroundColor,plot,plot.a);
    //fragColor = vec4(0.0);
}
