void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = fragCoord.x - (iResolution.x / 2.);
    x /= (iResolution.x / 2.) / 10.;
    float y = fragCoord.y - (iResolution.y / 2.);
    y /= (iResolution.y / 2.) / 10.;

    //float z = (1.-pow(x, 2.)) * (1.-pow(y, 2.));
    float z = sin(x) - sin (y);
    z = z < -0.05 ? sin(x) - sin (y): z;

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color = vec4(uv,0.5+0.5*sin(iTime),1.0);
    //vec4 color = vec4(0., 0., 0., 0.);
    vec4 noColor = vec4(1., 1., 1., 1.);
    if(mod(z, 1. - cos(iTime)/4.) < .4 + sin(iTime)/5. && (z > 0.05 || z < -0.05)){
        fragColor = color;
    }else{
    	fragColor = noColor;    
    }
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}
