// === icon message: "sound in". link: https://www.shadertoy.com/view/MtyXRW

float message(vec2 uv) { // to alter in the icon
    uv-=vec2(1.,10.); if ((uv.x<0.)||(uv.x>=32.)||(uv.y<0.)||(uv.y>=3.)) return -1.; 
    int i=1, bit=int(pow(2.,floor(32.-uv.x)));
    if (int(uv.y)==2) i=  928473456/bit; // 00110111 01010111 01100001 01110000
    if (int(uv.y)==1) i=  626348112/bit; // 00100101 01010101 01010000 01010000
    if (int(uv.y)==0) i= 1735745872/bit; // 01100111 01110101 01100001 01010000
 	return float(i-2*(i/2));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
    if (iResolution.y<2000.) {float c=message(fragCoord.xy/8.);if(c>=0.){fragColor=vec4(c);return;}}  

    vec2 uv = fragCoord.xy / iResolution.xy;

    float relativeFrameBoderThickness = DimGold*DimGold;
    vec3 frameBorderColor = vec3(DimGold);

    float ratio = iResolution.x/iResolution.y;
    float radius = iResolution.y / 2.;    
    vec2 center = iResolution.xy / 2.;
    vec2 distCenter = fragCoord - center;
    vec2 right = vec2 (1., 0.);

    float borderThickness = 42.1337;
    vec3 borderColor = vec3(InvGold, DimGold, InvGold);

    float scaledTime = iTime * TimeScale;    
    float angle = angleBetween (right, distCenter);
    float weightA = abs(fract(scaledTime + 3.0 * scaledTime));
    float weightB = abs(tan(scaledTime) * cos(scaledTime));
    float zoom = 123. * abs(0.5*sin(scaledTime)+.5)+250.;

    float theta = abs(sin(angle)-fract(angle)+cos(angle))/2.;
    weightA = mix(
        weightA, weightB,
        getWeightAlpha(scaledTime)
    );
    weightB = mix(
        weightB, weightA,
        getWeightAlpha(scaledTime)
    );
    float s = shape (theta, weightA, weightB);
    float r = zoom * s;
    float d = length (distCenter);
    float rd = r - d;
    float rdb = r - d + borderThickness;

    vec3 shapeColor = vec3(theta, weightA, weightB);
    vec3 backgroundColor = 
        vec3(DimGold)
      + DimGold*texture(iChannel0, uv+vec2(scaledTime, sin(scaledTime))).rrr;

    if (0. < rd) {
        fragColor = vec4 (shapeColor, 1.0);
    }
    else if (0. < rdb) {
        float broderGradient = rdb / borderThickness;
        fragColor = vec4(mix(shapeColor, backgroundColor, 1.-broderGradient), 1.);
    }
    else {
        fragColor = vec4 (backgroundColor, 1.);
    }

    float borderInlfuence = 0.1;
    if (uv.x < relativeFrameBoderThickness
     || uv.y < relativeFrameBoderThickness*ratio) {
        fragColor = vec4(frameBorderColor, borderInlfuence);
    }

    if ((uv.x + relativeFrameBoderThickness) > 1.) {
        fragColor = vec4(frameBorderColor, borderInlfuence);
    }
    if ((uv.y + relativeFrameBoderThickness*ratio) > 1.) {
        fragColor = vec4(frameBorderColor, borderInlfuence);
    }
}

#define PI 3.1415

const float Gold = (1. + sqrt(5.))/2.;
const float InvGold = 1. / Gold;
const float OneMinusInvGold = 1. - InvGold;
const float DimGold = OneMinusInvGold*OneMinusInvGold;
const float TimeScale = InvGold;

float getWeightAlpha(float t) {
    return (fract(t)+cos(t))/2.;
}

float shape (float theta, float weightA, float weightB) {
    float offset = PI / 3.0;
    return cos(theta + offset * weightA) * sin (theta + offset * weightB);
}

float angleBetween (vec2 a, vec2 b) {
    float cosTheta = dot (a, b) / (length (a) * length (b));

    return acos (cosTheta);
}

vec2 mainSound( int samp, float time )
{    
    float angle = float(samp);
    float scaledTime = time * TimeScale;

    float weightA = abs(fract(scaledTime + 3.0 * scaledTime));
    float weightB = abs(tan(scaledTime) * fract(scaledTime));
    float zoom = 2. * abs(0.5*sin(scaledTime)+.5)+5.;

    float s = shape (abs(tan(angle)), weightA, weightB);
    float r = zoom * s;

    vec2 k = mix(
        vec2( weightB, weightA ),
        vec2( weightA, weightB ),
        getWeightAlpha(scaledTime)
    );

    return 2.*abs(cos(scaledTime)) * mix(k, s * k, zoom);
}
