void mainImage(out vec4 O, vec2 I )
{
	O =texture(iChannel0,I/iResolution.xy);
}

float hash( float n )
{
    return (fract(iTime+sin(n+iDate.w)*43758.5453)-.5);
}
float logmap(vec2  pl)
{
    float u = .5+.5*cos(iTime),
        a=0.;
    for(int i = 0;i<64;i++)
    {      
            a+=step(abs((u=pl.x*u*(1.-u))-pl.y),.001);
    }
    return pow(a/64.,.6);   
}

void mainImage( out vec4 O,  vec2 U )
{
	vec2 R = iResolution.xy,
        uv = (U+vec2(hash(U.x),hash(U.x+.5)) ) /R * vec2(4,1);
	O = max( texture(iChannel0, U / R), vec4(logmap(uv)) );
}
