#define PI 3.14

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color;
    color.rg = vec2(0.0,1.0);
    color[2] = 1.0;
    color[3] = 1.0;
    vec4 c1 = vec4(1.000,0.969,0.898,1.0);
    vec4 c2 = vec4(0.855,0.992,1.000,1.0);
    c1 = vec4(0.882,1.000,0.196,1.0);
    c2 = vec4(0.694,0.004,0.855,1.0);

    float time = fract(iDate.w*0.3);
    float crv = exp(-10.0*pow(time*2.-1.0,15.0));
	crv = abs(pow(sin(time*10.0*PI),0.4))*(1.0-pow(floor(time*10.0)*0.1, 0.45));;

	fragColor = mix(c2,c1,crv);
}
