// Made by omgs 2017

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 col=vec3(0.75);

    //background lines
    if(mod(uv.y,0.01) <0.0003 && uv.x<0.8 && uv.x > 0.1) col = vec3(0.);
    if(mod(uv.y,0.0401) <0.0003 && uv.x > 0.9) col = vec3(1.);
    float r=0.;
    uv.x = uv.x-0.5;  uv.x*=1.5;
    uv.y = uv.y-0.5;
    vec2 uvOrg = uv;
    //hair
    uv.y+=0.08;
    uv.x*=1.2-uv.y*0.5;
    r = 0.035 + 0.1*cos( -atan(uv.y,uv.x)*10.0 + 20.0*uv.x + 13. +uv.y)*0.6 +uv.y*0.3;
    if(length(uv)-0.3 <r) col=vec3(.74,.96,1.);
    if(length(uv)-0.3 <r && length(uv)-r>0.295) col=vec3(0.);
    uv.y-=0.08;

    //earR
    uv=uvOrg;
    r = 0.1 ;
    uv.y+=0.06; uv.x*=1.1;uv.y*=.9;
    uv.x+=0.04-cos(uv.y*15.)*0.15;
    if(length(uv)-r<0.0 && uv.x >0.05)col=vec3(1.,219./255.,185./225.)+vec3(0.09);
     if(length(uv)-r<0.0 && length(uv)-r>-0.004 && uv.x >0.05 )col=vec3(0.0);
    //earL
    uv=uvOrg;
    r = 0.1 ;
    uv.y+=0.08; uv.x*=1.1;uv.y*=.9;
    uv.x-=0.055-cos(uv.y*15.)*0.15;
    if(length(uv)-r<0.0 && uv.x <-0.03)col=vec3(1.,219./255.,185./225.)+vec3(0.09);
    if(length(uv)-r<0.0 && length(uv)-r>-0.004 && uv.x <-0.03)col=vec3(0.0);
    uv=uvOrg;

    //labcoat    
    r = 0.2;
    uv.x*=0.8;
    uv.y+=0.5;
    uv.x-=0.019;
    if(length(uv)-r<0.0 )col=vec3(1.,219./255.,185./225.)+vec3(1.);
    if(length(uv)-r<0.0 && length(uv)-r>-0.004 )col=vec3(0.0);
    uv=uvOrg;
    if(uv.y < -0.4 && uv.y>-0.402 && uv.x<0.23 && uv.x>-0.18) col=vec3(0.);
    uv.y+=0.345;
    if(uv.y < -0.4+uv.x*1.5 && uv.y>-0.403+uv.x*1.5 && uv.x<0.23 && uv.x>-0.18) col=vec3(0.);
    uv=uvOrg;
    uv.x-=0.0;
    uv.y+=0.27;
    if(uv.y < -0.4-uv.x*1.5 && uv.y>-0.403-uv.x*1.5 && uv.x<0.23 && uv.x>-0.18) col=vec3(0.);
    //shirt  
    uv=uvOrg;
    r = 0.2;
    uv.x*=2.1;
    uv.y+=0.45;
    uv.x-=0.035;
    if(length(uv)-r<0.0 )col=vec3(.74,.96,1.);
    if(length(uv)-r<0.0 && length(uv)-r>-0.004 )col=vec3(0.0);
    uv=uvOrg;

    //neck    
    r = 0.07 ;
    uv.y+=0.3;
    uv.x-=0.019;
    if(length(uv)-r<0.0 )col=vec3(1.,219./255.,185./225.)+vec3(0.09);
    if(length(uv)-r<0.0 && length(uv)-r>-0.002 )col=vec3(0.0);
    uv=uvOrg;

    //face
    uv.x*=1.3; uv.y*=1.1;
    r=0.35*cos(uv.x*4.5);
    uv.y+=0.1; uv+=-cos(0.5+uv.x)*0.1+sin(0.1+uv.y)*0.15;
    uv.x+=0.05;
    r+=0.01;
    if(length(uv)-r<0.) col=vec3(1.,219./255.,185./225.)+vec3(0.09);
    r-=0.02;
    if(length(uv)-r<0.027 &&length(uv)-r>0.022 ) col=vec3(0.);


    //reye
    r=0.06;
    uv.y+=0.065;
    uv+=-cos(0.1-uv.x)*0.1+sin(0.1-uv.y)*0.22;
    if(length(uv)-r<0.027 &&length(uv)-r>0.023 ) col=vec3(0.);

     //rlid

    if(length(uv)-r<0.027 &&length(uv)-r<0.023 && uv.y<cos(-0.72+uv.x*20.)*.015)col=vec3(1.);
    if(length(uv)-r<0.027 &&length(uv)-r<0.023 && uv.y<cos(-0.72+uv.x*20.)*.015
      && uv.y>cos(-.72+uv.x*20.)*.015-0.005)col=vec3(0.);

    //leye
    r=0.055;
    uv=uvOrg;
    uv.x+=0.07;
    uv.x*=1.2;
    uv.y+=0.02;
    uv.y*=0.9;
    if(length(uv)-r<0.027 &&length(uv)-r>0.023 ) col=vec3(0.);
    //Llid

    if(length(uv)-r<0.027 &&length(uv)-r<0.023 && uv.y<cos(0.855+uv.x*25.)*.015)col=vec3(1.);
    if(length(uv)-r<0.027 &&length(uv)-r<0.023 && uv.y<cos(0.855+uv.x*25.)*.015
      && uv.y>cos(0.855+uv.x*25.)*.015-0.005)col=vec3(0.);

    //Rpup
    uv = uvOrg;
    uv.y+=0.014;
    uv.x-=0.075-cos(iDate.w*0.3)*0.01;
    uv.y+=0.027-cos(iDate.w*0.3)*0.01;
    if(length(uv)-0.01<0.) col= vec3(0.);
    //Lpup
    uv = uvOrg;
    uv.x+=0.075+cos(iDate.w*0.3)*0.01;
    uv.y+=0.045-+cos(iDate.w*0.3)*0.01;
    if(length(uv)-0.01<0.) col= vec3(0.);

    // mouth
    uv=uvOrg;
    uv+=-cos(-uv.x*5.)*0.1+sin(0.1-uv.y*2.)*0.2;
    if(uv.x<0.1-cos(iDate.w*0.1)*0.02 && uv.x>-0.06+cos(iDate.w*0.1)*0.02 && uv.y<-0.2 && uv.y>-0.205) col=vec3(0.);

    // brow    
    uv=uvOrg;
    uv.y-=0.35;
    uv.x+=0.035;
    uv.x*=1.2;
    uv+=-cos(0.9-uv.x)*0.2+sin(0.1-uv.y)*0.5;
    if(uv.x<0.17 && uv.x>-0.04 && uv.y<-0.2+cos(uv.x*24.3)*0.01 && uv.y>-0.215+cos(uv.x*24.3)*0.01) 
        col=vec3(.74,.96,1.);
      if(uv.x<0.17 && uv.x>-0.04 && uv.y>-0.215+cos(uv.x*24.3)*0.01 && uv.y<-0.213+cos(uv.x*24.3)*0.01) 
        col=vec3(0.);
     uv.y+=0.02;
     if(uv.x<0.17 && uv.x>-0.04 && uv.y<-0.18+cos(uv.x*24.3)*0.01 && uv.y>-0.182+cos(uv.x*24.3)*0.01) 
        col=vec3(0.);

    //Fwrinkle 1
    uv=uvOrg;
     uv+=-cos(-0.5+uv.x)*0.3+sin(0.1+uv.y*3.)*0.2;
    uv.y-=0.2;
    uv.x+=0.2;
      if(uv.x<0.1 && uv.x>-0.05 && uv.y>-0.215+cos(uv.x*24.3)*0.02 && uv.y<-0.21+cos(uv.x*24.3)*0.02) 
        col=vec3(0.4);
     //Fwrinkle2
    uv.y-=0.015;
      if(uv.x<0.1 && uv.x>-0.05 && uv.y>-0.215+cos(uv.x*24.3)*0.02 && uv.y<-0.21+cos(uv.x*24.3)*0.02) 
        col=vec3(0.4);

    //Ewrinkle 1
    uv=uvOrg;
    r=0.05;
    uv.y+=0.05;
    uv.x+=0.07;
    if(length(uv)-r<0.027 &&length(uv)-r>0.022 && uv.y <-0.06) col=vec3(0.4);

    //Ewrinkle R
    uv=uvOrg;
    r=0.05;
    uv.y+=0.04;
    uv.x-=0.09;
    if(length(uv)-r<0.027 &&length(uv)-r>0.022 && uv.y <-0.06) col=vec3(0.4);

   //MwrinkleR
    uv=uvOrg;
    r=0.005;
    uv.y+=0.21;
    uv.x-=0.087-cos(iDate.w*0.1)*0.025;
    if(length(uv)-r<0.02 &&length(uv)-r>0.017 && uv.x>0.01) col=vec3(0.);

    //MwrinkleL
    uv=uvOrg;
    r=0.005;
    uv.y+=0.21;
    uv.x+=0.055-cos(iDate.w*0.1)*0.025;
    if(length(uv)-r<0.02 &&length(uv)-r>0.017 && uv.x<-0.01) col=vec3(0.);

    //Drool
    uv=uvOrg;
    r = (0.05-cos(iDate.w*0.1)*0.01) - 0.04*cos( -atan(uv.y,uv.x)*50.0 )*.2 ;
    uv.y+=0.21;
    uv.x*=2.;
    uv.x+=0.02-cos(uv.y*15.)*0.01;
    if(length(uv)-r<0.0 && uv.y < 0.01) col=vec3(0.,.9,0.)+0.5;
    if(length(uv)-r<0.0 && length(uv)-r>-0.001 && uv.y < 0.01) col=vec3(0.);

    //nose
    uv=uvOrg;
    r=0.02;
    uv.y+=0.13;
    uv.x*=2.5;
    uv.x-=0.024;
    if(length(uv)-r<0.02 &&length(uv)-r>0.016 && uv.y<0.02) col=vec3(0.);

    //fly
    uv=uvOrg;
    r=0.012;
    uv.x+=0.5+cos(iDate.w)*0.1+ sin(iDate.w)*0.02;
    uv.y+=-0.2+cos(iDate.w)*0.1 + sin(iDate.w)*0.01;
    if(length(uv)-r <0.) col=vec3(0);
    r=0.008;
    uv.y-=0.012 +cos(iDate.w*150.)*0.005; 
    if(length(uv)-r <0.) col=vec3(1);

    if(mod(cos(iDate.w*45.),0.0)<0. && cos(iDate.w*0.5)>0.999)col=vec3(1.);

    fragColor = vec4(col,1.0);
}
