float bayerMatrix9[9u] = float[](
    0.0, 7.0, 3.0,
    6.0, 5.0, 2.0,
    4.0, 1.0, 8.0
);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec3 col = vec3(0.0, 0.0, 0.0);

    int t = int(iTime*2.0);

    ivec2 icoord = ivec2(int(fragCoord.x), int(fragCoord.y));

    for(int i = 0; i < t % 9 + 1; i++){

        int index = int(bayerMatrix9[i]);

        if( (icoord.x + 3*icoord.y) % 9 == index )
        {
           //WRITE THE PIXEL
           col = vec3(1.0, 1.0, 1.0);

        }else{ //if (the previous texture isn't out of the pixel)
            //pick the sample from previous full texture   
        }

    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
