void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    //normalize the xy space 0.0 - 1.0 in one line
	vec2 xy = fragCoord.xy / iResolution.xy;

    //get the pixel at the xy from the image
    vec4 texColor = texture(iChannel0, xy);
 /*

	//the important part to remember is when an rgb value
	//is 1.0 = 255 (full), 0.0 = 0 (black)
	//when all are 1.0, color = white


	//all colors increase to white the same
	//Subtraction wouold cycle to black
    texColor.r += abs(cos(iTime));
    texColor.g += abs(cos(iTime));
    texColor.b += abs(cos(iTime));

    //each color fades in and out on in it's own period
	//cycles through all colors
   	texColor.r *= abs(cos(iTime));
    texColor.g *= abs(sin(iTime));
    texColor.b *= abs(cos(iTime)) * abs(sin(iTime));
    */

    //Cycle from color to greyscale
    //dot product of original vec3
	float gray = dot(texColor.rgb, vec3(0.299, 0.587, 0.114));
	//keep original alpha
    vec4 greyscale = vec4(vec3(gray), texColor.a);

    //multiplying the oscillating sin value to the original 
    // color channels fades them to black. Max(), limits the values
    texColor *= max(
        vec4(
        vec3(abs(sin(iTime)), 
             abs(sin(iTime)),
             abs(sin(iTime))),
        1.0),
    greyscale);


    fragColor = texColor;
}
