#define backbuffer(uv) texture(iChannel0,uv)
#define video(uv) texture(iChannel1,uv)

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	fragColor = mix(video(uv), backbuffer(uv), 0.5);
}

#define FRAME_AGE 100

#define backbuffer(uv) texture(iChannel0,uv)
#define video(uv) texture(iChannel1,uv)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    bool persist = iFrame % FRAME_AGE == 0;

    fragColor = persist ? video(uv) : backbuffer(uv);
}
