void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         M = iMouse.xy;
    if ( U.x-.5 == R.x/2. ) { O=vec4(1); return; } // separator
    U = ( U+U - R ) / R.y;

    float l = length(U), 
          V = U.x< 0. ? l : 1./l,                 // tangential speed
          a = atan(U.y,U.x),                    
          t = sin(iTime);                         // amount: time or mouse.x
    if ( length(M) > 10. ) t = 3.* (2.*M.x/R.x-1.);

    a += .2* t * V/l; U = l* vec2(cos(a),sin(a)); // displacement (analytical)
  //U += .2* t * V* vec2(-U.y,U.x)/l;             //              (approx if iterative)

    if ( cos(iTime) < 0. ) 
         O = texture(iChannel0, .5+.5*U);
    else U = fract(16.*U)-.5, 
         O = vec4(.5+.5*sign(U),1,0) *.1 / dot(U,U);
}
