float jifen(float x, float y0){
	return log(x + sqrt(x*x + y0*y0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float x0 = uv.x - 0.5;
    float y0 = uv.y - 0.5;
    float l = 0.2;
    float b1 = jifen(l - x0, y0 - 0.05) - jifen(-l - x0, y0 - 0.05);
    float b2 = jifen(l - x0, y0 + 0.05) - jifen(-l - x0, y0 + 0.05);
    // Output to screen
    // fragColor = vec4(col,1.0);
    fragColor = vec4(1.0);
    col.r = 1.0;
    fragColor.g = mod(sqrt(abs(b1 - b2)* 50.0 * col.r), 1.0);
}
