float K = 10.0;

vec2 ellipse(float a, float b, float t)
{
    return vec2(a * cos(t), b * sin(t)) + vec2(0.5, 0.5);
}

vec2 coloumb(vec2 p0, float q0, vec2 p1, float q1)
{
    return (p1 - p0) * 
        ( (K * abs(q0 * q1)) / pow(distance(p0, p1), 2.0) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float q0 = 1.0; float q1 = -0.25; float q2 = 0.4; float q3 = -0.5;
    vec2 p1 = ellipse(0.4, 0.4, 0.2 * iTime);
    vec2 p2 = ellipse(0.21, 0.21, 2.0 * iTime);
    vec2 p3 = ellipse(0.4, -0.3, 0.9 * iTime);
    vec2 m = coloumb(uv, q0, p1, q1) + coloumb(uv, q0, p2, q2) + coloumb(uv, q0, p3, q3);

    vec4 col = length(m) * 0.02 * texture(iChannel0, m * 0.2);

    float dy = length(normalize(dFdy(texture(iChannel0, m * 0.2).xyz)));
    float dx = length(normalize(dFdx(texture(iChannel0, m * 0.2).xyz)));

    vec3 n = normalize( cross(dx * vec3(1.0, 0.0, 0.0), dy * vec3(0.0, 1.0, 0.0)) );
    vec3 l = vec3(m, 0.9);
    float lambert = clamp(0.0, 1.0, dot(n, l));


	fragColor = clamp(lambert * col, vec4(0.0), vec4(1.0));
}
