vec3 lum = vec3(0.2126,0.7152,0.0722);
float fade = 0.05;
float lineWidth = 0.0008;
float lineSpeed = 0.001;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = fragCoord.xy / iResolution.xy;

    float rat = iResolution.y / iResolution.x;

    float gray = pow( dot(texture(iChannel0, uv.xy).rgb, lum), 0.3 );
    vec3 color = vec3( 0.3, 1.0, 0.3 ) * gray;

    if( mod( uv.y + iTime * lineSpeed, lineWidth ) < lineWidth * 0.5  )
        color *= 0.8;

    float radius = distance( vec2( uv.x, ( uv.y - 0.5 ) *  rat + 0.5 ) , vec2(0.5, 0.5) );
    if( radius > 0.5 - fade )
    {
        if( radius < 0.5 )
        {
            color *= pow( 1.0 - ( ( radius - ( 0.5 - fade ) ) / fade  ), 1.5 );
        }
        else
        {
     		color = vec3( 0.0, 0.0, 0.0 ); 
        }
    }

    fragColor = vec4(color, 1);
}
