void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float addVal = 0.3;
    vec4 add = vec4(addVal, addVal, addVal, 0.0);
   vec4 color = vec4(0.0,0.0,0.0,0.0);

    color = texture(iChannel0, vec2(1.0 - (uv.x/0.5), uv.y/0.5)) + add;

    fragColor = color;
}
