vec3 hsvtorgb(vec3 hsv) {
    hsv.r *= 360.0;

    if (hsv.r >= 360.0) hsv.r = 0.0;

    hsv.r /= 60.0;

    int i = int(hsv.r);
    float f = fract(hsv.r);

    float p = hsv.b * (1.0 - hsv.g);
    float q = hsv.b * (1.0 - hsv.g * f);
    float t = hsv.b * (1.0 - hsv.g * (1.0 - f));

    switch(i) {
    case 0:
    	return vec3(hsv.b, t, p);
    case 1:
    	return vec3(q, hsv.b, p);
    case 2:
    	return vec3(p, hsv.b, t);
    case 3:
    	return vec3(p, q, hsv.b);
    case 4:
    	return vec3(t, p, hsv.b);
    case 5:
    	return vec3(hsv.b, p, q);
    default:
    	return vec3(hsv.b, hsv.b, hsv.b);
    }

	return hsv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv.y = 1.0 - uv.y;

    int tx = int(uv.y*16.0)*32 + int(uv.x*32.0);

    float h  = float(tx) / 512.0; //texelFetch( iChannel0, ivec2(tx, 0), 0 ).x;
    float s  = 0.5 + 0.5 * texelFetch( iChannel0, ivec2(tx, 0), 0 ).x;
    float v  = 0.1 + 0.9 * texelFetch( iChannel0, ivec2(tx, 0), 0 ).x;

	fragColor = vec4(hsvtorgb(vec3(h, s, v)), 1.0);
}
