#define M_PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = -1. + 2. * fragCoord.xy / iResolution.xy;
//    vec2 cc = vec2(-cos(iTime) * .5, sin(iTime) * .5);
    vec2 cc = vec2(-.4, .6);
    float dmin = 1000.;
    for(int i = 0; i<64; i++)
    {
        uv = cc + vec2(uv.x * uv.x - uv.y * uv.y, 2. * uv.x * uv.y);
        float m2 = dot(uv, uv);
        if(m2 > 100.) break;
        dmin = min(dmin, m2);
    }

    float c = sqrt(dmin);

	fragColor = vec4(c, c, c ,1.0);
}
