void mainImage( out vec4 f, in vec2 w ) {
    vec4 p = vec4(w,0.,1.)/iResolution.x-.5, d=p, t, c;
    float T = iMouse.x*.01, C = cos(T), S = sin(T);
    p.z -= 5.;
    p.y -= 1.8;
    for(float i = 2.; i >0.; i-=.01)
    {
        c = texture(iChannel0, mat3(C,0,S, 0,1,0, -S,0,C) * -p.xyz);
        t = length(p.xyz)-2.2 + c*.5;
        if(t.x<.01) break;
        p -= d*t.x*.2;
        c*=.5;
     }
     f = c;
}
